{"changed":true,"filter":false,"title":"PostController.php","tooltip":"/app/Http/Controllers/PostController.php","value":"<?php\n\nnamespace App\\Http\\Controllers;\n\nuse Illuminate\\Http\\Request;\n\nuse App\\Http\\Requests;\nuse App\\Http\\Controllers\\Controller;\nuse App\\Post;\nuse App\\Category;\nuse Session;\n\nclass PostController extends Controller\n{\n    public function __construct (){\n        $this->middleware('auth');\n    }\n    /**\n     * Display a listing of the resource.\n     *\n     * @return \\Illuminate\\Http\\Response\n     */\n    public function index()\n    {\n        $posts = Post::orderBy('id', 'desc')->paginate(10);\n        return view('posts.index')->withPosts($posts);\n    }\n\n    /**\n     * Show the form for creating a new resource.\n     *\n     * @return \\Illuminate\\Http\\Response\n     */\n    public function create()\n    {\n        $categories = Category::all();\n        return view('posts.create')->withCategories($categories);\n    }\n\n    /**\n     * Store a newly created resource in storage.\n     *\n     * @param  \\Illuminate\\Http\\Request  $request\n     * @return \\Illuminate\\Http\\Response\n     */\n    public function store(Request $request)\n    {\n        //Validate\n        $this->validate($request,array(\n                'title' => 'required|max:255',\n                'slug' => 'required|alpha-dash|min:5|max:255|unique:posts,slug',\n                'category_id' => 'required',\n                'body' => 'required'\n            ));\n        //Stor in DB\n        $post = new Post;\n        $post->title = $request->title;\n        $post->slug = $request->slug;\n        $post->category_id = $request->category;\n        $post->body = $request->body;\n        \n        $post->save();\n        \n        Session::flash('success','The blog post successfully saved!');\n        //Redirect\n        return redirect()->route('posts.show', $post->id);\n        \n        \n    }\n\n    /**\n     * Display the specified resource.\n     *\n     * @param  int  $id\n     * @return \\Illuminate\\Http\\Response\n     */\n    public function show($id)\n    {   \n        $post = Post::find($id);\n        return view('posts.show')->withPost($post);\n    }\n\n    /**\n     * Show the form for editing the specified resource.\n     *\n     * @param  int  $id\n     * @return \\Illuminate\\Http\\Response\n     */\n    public function edit($id)\n    {\n        $post = Post::find($id);\n        return view('posts.edit')->withPost($post);\n    }\n\n    /**\n     * Update the specified resource in storage.\n     *\n     * @param  \\Illuminate\\Http\\Request  $request\n     * @param  int  $id\n     * @return \\Illuminate\\Http\\Response\n     */\n    public function update(Request $request, $id)\n    {\n        $post = Post::find($id);\n        //Validate\n        if($post->slug == $request->input('slug'))\n        {\n            $this->validate($request,array(\n                    'title' => 'required|max:255',\n                    'body' => 'required'\n                ));\n        }\n        else{\n            $this->validate($request,array(\n                    'title' => 'required|max:255',\n                    'slug' => 'required|alpha-dash|min:5|max:255|unique:posts,slug',\n                    'body' => 'required'\n                ));\n        }\n        //Stor in DB\n        $post->title = $request->input('title');\n        $post->slug = $request->input('slug');\n        $post->body = $request->input('body');\n        $post->save();\n        \n        Session::flash('success','The blog post successfully saved!');\n        //Redirect\n        return redirect()->route('posts.show', $post->id);\n    }\n\n    /**\n     * Remove the specified resource from storage.\n     *\n     * @param  int  $id\n     * @return \\Illuminate\\Http\\Response\n     */\n    public function destroy($id)\n    {\n        $post = Post::find($id);\n        $post->delete();\n        Session::flash('success', 'The post successfully deleted!');\n        return redirect()->route('posts.index');\n    }\n}\n","undoManager":{"mark":97,"position":100,"stack":[[{"start":{"row":34,"column":39},"end":{"row":34,"column":40},"action":"remove","lines":["l"],"id":68}],[{"start":{"row":34,"column":38},"end":{"row":34,"column":39},"action":"remove","lines":[">"],"id":69}],[{"start":{"row":34,"column":37},"end":{"row":34,"column":38},"action":"remove","lines":["-"],"id":70}],[{"start":{"row":35,"column":35},"end":{"row":35,"column":36},"action":"insert","lines":["-"],"id":71}],[{"start":{"row":35,"column":36},"end":{"row":35,"column":37},"action":"insert","lines":[">"],"id":72}],[{"start":{"row":35,"column":37},"end":{"row":35,"column":38},"action":"insert","lines":["w"],"id":73}],[{"start":{"row":35,"column":38},"end":{"row":35,"column":39},"action":"insert","lines":["i"],"id":74}],[{"start":{"row":35,"column":39},"end":{"row":35,"column":40},"action":"insert","lines":["t"],"id":75}],[{"start":{"row":35,"column":40},"end":{"row":35,"column":41},"action":"insert","lines":["h"],"id":76}],[{"start":{"row":35,"column":41},"end":{"row":35,"column":42},"action":"insert","lines":["C"],"id":77}],[{"start":{"row":35,"column":42},"end":{"row":35,"column":43},"action":"insert","lines":["a"],"id":78}],[{"start":{"row":35,"column":43},"end":{"row":35,"column":44},"action":"insert","lines":["t"],"id":79}],[{"start":{"row":35,"column":44},"end":{"row":35,"column":45},"action":"insert","lines":["e"],"id":80}],[{"start":{"row":35,"column":45},"end":{"row":35,"column":46},"action":"insert","lines":["g"],"id":81}],[{"start":{"row":35,"column":46},"end":{"row":35,"column":47},"action":"insert","lines":["o"],"id":82}],[{"start":{"row":35,"column":47},"end":{"row":35,"column":48},"action":"insert","lines":["r"],"id":83}],[{"start":{"row":35,"column":48},"end":{"row":35,"column":49},"action":"insert","lines":["i"],"id":84}],[{"start":{"row":35,"column":49},"end":{"row":35,"column":50},"action":"insert","lines":["e"],"id":85